import static org.hamcrest.core.Is.is;
import static org.hamcrest.core.IsNull.notNullValue;
import static org.junit.Assert.assertThat;
import static org.junit.Assert.fail;

import java.util.HashMap;
import java.util.Map;
import org.junit.Test;


public class ApplePayUtilsTest {

  @Test
  public void testDecryptAsBytes_Ok() {
    try {
      byte[] bytes = ApplePayUtils.decryptAsBytes(
          "MFkwEwYHKoZIzj0CAQYIKoZIzj0DAQcDQgAEl/XAbOgrSCupps/QbIxJ3u4QZ1PlbO5uGDD1zj/JGMoephYSEgw+63gHQHekx3T8duXN3CoYafUpuQlwOeK6/w==",
          "MIGHAgEAMBMGByqGSM49AgEGCCqGSM49AwEHBG0wawIBAQQgjyo3fzxT7j+CFxC7I4B5iVee2FUyn2vfOSjcgp2/g6qhRANCAARdoBFEtnuapXFKw4DYWsW0yV4bavpdWKszkefi19AhlIRE3WSNWSn25W5tZNFjMWtLISBmqANyufx2xP19oRvy",
          "",
          "");
      assertThat(bytes, is(notNullValue()));
    } catch (Exception exception) {
      fail("The decryption must run successfully. Exception found: " + exception.getMessage());
    }
  }

  @Test
  public void testDecryptAsString_Ok() {
    try {
      String string = ApplePayUtils.decryptAsString(
          "MFkwEwYHKoZIzj0CAQYIKoZIzj0DAQcDQgAEl/XAbOgrSCupps/QbIxJ3u4QZ1PlbO5uGDD1zj/JGMoephYSEgw+63gHQHekx3T8duXN3CoYafUpuQlwOeK6/w==",
          "MIGHAgEAMBMGByqGSM49AgEGCCqGSM49AwEHBG0wawIBAQQgjyo3fzxT7j+CFxC7I4B5iVee2FUyn2vfOSjcgp2/g6qhRANCAARdoBFEtnuapXFKw4DYWsW0yV4bavpdWKszkefi19AhlIRE3WSNWSn25W5tZNFjMWtLISBmqANyufx2xP19oRvy",
          "",
          "");
      assertThat(string,
          is("{\"applicationPrimaryAccountNumber\":\"5186009300800808\",\"applicationExpirationDate\":\"191031\",\"currencyCode\":\"840\",\"transactionAmount\":500,\"deviceManufacturerIdentifier\":\"050110030273\",\"paymentDataType\":\"EMV\",\"paymentData\":{\"emvData\":\"nyYIM2LtbOmXgn2fEBIBFKUAAAAAAAAAAAAAAAAAAACfNgIAA5UFAAAAAACfJwGAnzQDAQACnzcE0qE2H58CBgAAAAAFAJ8DBgAAAAAAAF8qAghAmgMWCAicAQCEB6AAAAAEEBBaCFGGAJMAgAgIXzQBAF8kAxkQMZ8aAghAggICgA==\"}}"));
    } catch (Exception exception) {
      fail("The decryption must run successfully. Exception found: " + exception.getMessage());
    }
  }

  @Test
  public void testDecryptAsHashMap_Ok() {
    try {
      HashMap map = ApplePayUtils.decryptAsObjectFromJSON(HashMap.class,
          "MFkwEwYHKoZIzj0CAQYIKoZIzj0DAQcDQgAEl/XAbOgrSCupps/QbIxJ3u4QZ1PlbO5uGDD1zj/JGMoephYSEgw+63gHQHekx3T8duXN3CoYafUpuQlwOeK6/w==",
          "MIGHAgEAMBMGByqGSM49AgEGCCqGSM49AwEHBG0wawIBAQQgjyo3fzxT7j+CFxC7I4B5iVee2FUyn2vfOSjcgp2/g6qhRANCAARdoBFEtnuapXFKw4DYWsW0yV4bavpdWKszkefi19AhlIRE3WSNWSn25W5tZNFjMWtLISBmqANyufx2xP19oRvy",
          "",
          "");
      assertThat(map.get("applicationPrimaryAccountNumber"), is("5186009300800808"));
      assertThat(map.get("applicationExpirationDate"), is("191031"));
      assertThat(map.get("currencyCode"), is("840"));
      assertThat(map.get("transactionAmount"), is(500.0));
      assertThat(map.get("deviceManufacturerIdentifier"), is("050110030273"));
      assertThat(map.get("paymentDataType"), is("EMV"));
      Map paymentData = (Map) map.get("paymentData");
      assertThat(paymentData.get("emvData"),
          is("nyYIM2LtbOmXgn2fEBIBFKUAAAAAAAAAAAAAAAAAAACfNgIAA5UFAAAAAACfJwGAnzQDAQACnzcE0qE2H58CBgAAAAAFAJ8DBgAAAAAAAF8qAghAmgMWCAicAQCEB6AAAAAEEBBaCFGGAJMAgAgIXzQBAF8kAxkQMZ8aAghAggICgA=="));
    } catch (Exception exception) {
      fail("The decryption must run successfully. Exception found: " + exception.getMessage());
    }
  }

  @Test(expected = Exception.class)
  public void testDecrypt_Invalid_Key() throws Exception {
    ApplePayUtils.decryptAsBytes(
        "MFkwEwYHKoZIzj0CAQYIKoZIzj0DAQcDQgAEl/XAbOgrSCupps/QbIxJ3u4QZ1PlbO5uGDD1zj/JGMoephYSEgw+63gHQHekx3T8duXN3CoYafUpuQlwOeK6/w==",
        "MIGHAgEAMBMGByqGSM49AgEGCCqGSM49AwEHBG0wawIBAQQgiMLkP3TEHUhfB+ml14GJrgXHdEHN5M7g0Hst9FsltjqhRANCAAT25bMBEIRRfWRzMk0guyCL31o+M+HT7NmbAOxhTQ5WfYH8lwVW+Ywb+P/2lByWF7v28hy4VSydDxkhVEgvxFRQ",
        "",
        "");
  }

}
